/* 
   PIE CHART COMPONENT (SVG)
    */
function PieChart({ correct, incorrect, unattempted, size=220 }) {
  const total = correct + incorrect + unattempted;
  if (total === 0) return null;
  const cx = 110, cy = 110, outerR = 95, innerR = 58, labelR = 76;
  const segments = [
    { value: correct, color: "#16a34a", textColor: "#fff" },
    { value: incorrect, color: "#ef4444", textColor: "#fff" },
    { value: unattempted, color: "#d1d5db", textColor: "#555" },
  ];

  const polarToXY = (angle, r) => ({
    x: cx + r * Math.cos(angle),
    y: cy + r * Math.sin(angle),
  });

  let startAngle = -Math.PI / 2;
  const arcs = [];
  const labels = [];

  segments.filter(s => s.value > 0).forEach((seg, i) => {
    const sweep = (seg.value / total) * 2 * Math.PI;
    const endAngle = startAngle + sweep;
    const largeArc = sweep > Math.PI ? 1 : 0;
    const o1 = polarToXY(startAngle, outerR);
    const o2 = polarToXY(endAngle, outerR);
    const i1 = polarToXY(endAngle, innerR);
    const i2 = polarToXY(startAngle, innerR);
    const d = [
      `M ${o1.x} ${o1.y}`,
      `A ${outerR} ${outerR} 0 ${largeArc} 1 ${o2.x} ${o2.y}`,
      `L ${i1.x} ${i1.y}`,
      `A ${innerR} ${innerR} 0 ${largeArc} 0 ${i2.x} ${i2.y}`,
      `Z`
    ].join(" ");
    arcs.push(<path key={i} d={d} fill={seg.color} stroke="#fff" strokeWidth="2" />);

    // Label on the arc midpoint
    const midAngle = startAngle + sweep / 2;
    const lp = polarToXY(midAngle, labelR);
    if (seg.value > 0) {
      labels.push(
        <text key={`l${i}`} x={lp.x} y={lp.y} textAnchor="middle" dominantBaseline="middle"
          style={{ fontSize: 14, fontWeight: 800, fill: seg.textColor }}>{seg.value}</text>
      );
    }
    startAngle = endAngle;
  });

  const pctCorrect = total > 0 ? Math.round((correct / total) * 100) : 0;

  return (
    <div style={{ display:"flex", flexDirection:"column", alignItems:"center" }}>
      <svg width={size} height={size} viewBox="0 0 220 220">
        {arcs}
        {labels}
        <text x={cx} y={cy} textAnchor="middle" dominantBaseline="middle"
          style={{ fontSize:30, fontWeight:900, fill:C.gray800 }}>{pctCorrect}%</text>
      </svg>
      <div style={{ display:"flex", gap:20, marginTop:14, flexWrap:"wrap", justifyContent:"center" }}>
        {[{c:"#16a34a",l:"Correct"},{c:"#ef4444",l:"Incorrect"},{c:"#d1d5db",l:"Unattempted"}].map(x => (
          <div key={x.l} style={{ display:"flex", alignItems:"center", gap:6 }}>
            <div style={{ width:14, height:14, borderRadius:3, background:x.c }} />
            <span style={{ fontSize:13, color:C.gray600, fontWeight:500 }}>{x.l}</span>
          </div>
        ))}
      </div>
    </div>
  );
}

