/* 
   SCREEN: Exam (Test-taking Interface)
    */
function ExamScreen({ onSubmit }) {
  const questions = ALL_QUESTIONS;
  const [cur, setCur] = useState(0);
  const [answers, setAnswers] = useState({});
  const [marked, setMarked] = useState(new Set());
  const [section, setSection] = useState("Physics");
  const [showSummary, setShowSummary] = useState(false);
  const [startTime] = useState(Date.now());
  const [lastVisit, setLastVisit] = useState(Date.now());
  const [visited, setVisited] = useState(new Set([0]));
  const [questionTimes, setQuestionTimes] = useState({});
  const timeLeft = useTimer(1800, !showSummary);

  const navigate = (i) => {
    const now = Date.now();
    setQuestionTimes(prev => ({...prev, [cur]: (prev[cur]||0) + (now-lastVisit)/1000 }));
    setLastVisit(now);
    setCur(i);
    setVisited(prev => new Set([...prev, i]));
    setSection(SUBJECTS[Math.floor(i/5)]);
  };

  const getStatus = (i) => {
    if (marked.has(i) && answers[i]!==undefined) return "answered-marked";
    if (marked.has(i)) return "marked";
    if (answers[i]!==undefined) return "answered";
    if (visited.has(i)) return "not-answered";
    return "not-visited";
  };

  const statusMap = {
    "answered":        { bg:"#16a34a", border:"#16a34a", color:"#fff",     shape:"square" },
    "not-answered":    { bg:"#dc2626", border:"#dc2626", color:"#fff",     shape:"square" },
    "not-visited":     { bg:"#ffffff", border:C.gray300,  color:C.gray500, shape:"square" },
    "marked":          { bg:"#7c3aed", border:"#7c3aed", color:"#fff",     shape:"circle" },
    "answered-marked": { bg:"#7c3aed", border:"#16a34a", color:"#fff",     shape:"circle-ring" },
  };

  const doSubmit = () => {
    const elapsed = Math.round((Date.now()-startTime)/1000);
    let correct=0, wrong=0, unanswered=0;
    questions.forEach((q,i) => {
      if (answers[i]===undefined) unanswered++;
      else if (answers[i]===q.correct) correct++;
      else wrong++;
    });
    onSubmit({ answers, correct, wrong, unanswered, total:questions.length, questions, elapsed, questionTimes });
  };

  if (timeLeft <= 0 && !showSummary) doSubmit();

  if (showSummary) {
    const answered = Object.keys(answers).length;
    return (
      <div style={{ minHeight:"100vh", background:C.bg, fontFamily:"'General Sans', -apple-system, sans-serif" }}>
        <div style={{ background:C.white, borderBottom:`1px solid ${C.gray200}`, padding:"12px 24px", display:"flex", justifyContent:"space-between", alignItems:"center" }}>
          <span style={{ fontWeight:700, fontSize:16, color:C.gray900 }}>Exam Summary</span>
          <span style={{ fontSize:14, color:C.gray500 }}>Time remaining: {fmtTime(timeLeft)}</span>
        </div>
        <div style={{ maxWidth:600, margin:"40px auto", padding:"0 20px" }}>
          <Card>
            <div style={{ display:"grid", gridTemplateColumns:"repeat(3, 1fr)", gap:12, marginBottom:24 }}>
              {[
                ["Answered", Object.keys(answers).filter(k=>!marked.has(Number(k))).length, C.green],
                ["Not Answered", [...visited].filter(v=>answers[v]===undefined && !marked.has(v)).length, C.red],
                ["Not Visited", questions.length - visited.size, C.gray400],
                ["Marked for Review", [...marked].filter(m=>answers[m]===undefined).length, C.purple],
                ["Answered & Marked", [...marked].filter(m=>answers[m]!==undefined).length, C.green],
                ["Total", questions.length, C.primary],
              ].map(([l,v,c])=>(
                <div key={l} style={{ background:C.gray50, borderRadius:8, padding:14, textAlign:"center" }}>
                  <div style={{ fontSize:22, fontWeight:800, color:c }}>{v}</div>
                  <div style={{ fontSize:12, color:C.gray500, marginTop:2 }}>{l}</div>
                </div>
              ))}
            </div>
            <div style={{ display:"flex", gap:12 }}>
              <Btn variant="ghost" onClick={()=>setShowSummary(false)} style={{ flex:1, justifyContent:"center" }}> Back to Test</Btn>
              <Btn variant="danger" onClick={doSubmit} style={{ flex:1, justifyContent:"center" }}>Confirm End Exam</Btn>
            </div>
          </Card>
        </div>
      </div>
    );
  }

  return (
    <div style={{ minHeight:"100vh", background:C.bg, fontFamily:"'General Sans', -apple-system, sans-serif" }}>
      <div style={{ background:C.white, borderBottom:`1px solid ${C.gray200}`, padding:"10px 20px", display:"flex", justifyContent:"space-between", alignItems:"center", position:"sticky", top:0, zIndex:100 }}>
        <div style={{ display:"flex", alignItems:"center", gap:12 }}>
          <span style={{ fontWeight:700, color:C.gray900, fontSize:15 }}>CET Mock Test</span>
          <div style={{ display:"flex", gap:4 }}>
            {SUBJECTS.map(sub => (
              <button key={sub} onClick={()=>{setSection(sub); navigate(SUBJECTS.indexOf(sub)*5);}}
                style={{ padding:"6px 14px", borderRadius:6, fontSize:12, fontWeight:600, cursor:"pointer", border:"none",
                  background: section===sub ? C.primary : "transparent", color: section===sub ? "#fff" : C.gray500 }}>
                {sub}
              </button>
            ))}
          </div>
        </div>
        <div style={{ padding:"8px 16px", borderRadius:8, fontWeight:700, fontSize:16, fontFamily:"'JetBrains Mono', monospace",
          background: timeLeft<300 ? C.redBg : C.blue50, color: timeLeft<300 ? C.red : C.primary }}>
          {fmtTime(timeLeft)}
        </div>
      </div>

      <div style={{ display:"flex", maxWidth:1100, margin:"0 auto", gap:0, minHeight:"calc(100vh - 57px)" }}>
        <div style={{ flex:1, padding:"24px 28px" }}>
          <div style={{ display:"flex", justifyContent:"space-between", alignItems:"center", marginBottom:20 }}>
            <span style={{ fontSize:13, color:C.gray500 }}>Question {cur+1} of {questions.length}</span>
            <span style={{ fontSize:12, color:C.gray400, background:C.gray100, padding:"4px 10px", borderRadius:4 }}>{section}  {questions[cur].chapter}</span>
          </div>
          <Card style={{ marginBottom:20 }}>
            <h3 style={{ fontSize:16, fontWeight:600, color:C.gray800, lineHeight:1.7, marginTop:0, marginBottom:24 }}>
              Q{cur+1}. {questions[cur].q}
            </h3>
            <div style={{ display:"flex", flexDirection:"column", gap:10 }}>
              {questions[cur].options.map((opt,oi) => {
                const sel = answers[cur]===oi;
                return (
                  <div key={oi} onClick={()=>setAnswers(prev=>({...prev,[cur]:oi}))}
                    style={{ display:"flex", alignItems:"center", gap:14, padding:"13px 16px", borderRadius:10, cursor:"pointer", transition:"all 0.15s",
                      background: sel ? C.blue50 : C.white, border: `2px solid ${sel ? C.primary : C.gray200}` }}
                    onMouseEnter={e=>{if(!sel)e.currentTarget.style.borderColor=C.gray300}}
                    onMouseLeave={e=>{if(!sel)e.currentTarget.style.borderColor=sel?C.primary:C.gray200}}>
                    <div style={{ width:28, height:28, borderRadius:"50%", border:`2px solid ${sel?C.primary:C.gray300}`,
                      background: sel?C.primary:"transparent", display:"flex", alignItems:"center", justifyContent:"center",
                      color:sel?"#fff":C.gray500, fontSize:13, fontWeight:700, flexShrink:0, transition:"all 0.15s" }}>
                      {String.fromCharCode(65+oi)}
                    </div>
                    <span style={{ fontSize:14, color:C.gray700 }}>{opt}</span>
                  </div>
                );
              })}
            </div>
          </Card>
          <div style={{ display:"flex", gap:10, flexWrap:"wrap" }}>
            <Btn variant="ghost" disabled={cur===0} onClick={()=>navigate(cur-1)}> Previous</Btn>
            <Btn variant={marked.has(cur)?"outline":"ghost"} onClick={()=>setMarked(prev=>{const n=new Set(prev); n.has(cur)?n.delete(cur):n.add(cur); return n;})}
              style={{ color:C.purple, borderColor: marked.has(cur)?C.purple:C.gray200 }}>
              {marked.has(cur)?" Marked":" Mark for Review"}
            </Btn>
            {answers[cur]!==undefined && (
              <Btn variant="ghost" onClick={()=>setAnswers(prev=>{const n={...prev}; delete n[cur]; return n;})} style={{ color:C.red }}>Clear</Btn>
            )}
            <div style={{ flex:1 }} />
            {cur<questions.length-1 ? (
              <Btn onClick={()=>navigate(cur+1)}>Next </Btn>
            ) : (
              <Btn variant="danger" onClick={()=>setShowSummary(true)}>Submit Test</Btn>
            )}
          </div>
        </div>
        <div style={{ width:240, background:C.white, borderLeft:`1px solid ${C.gray200}`, padding:18 }}>
          <h4 style={{ fontSize:11, fontWeight:700, textTransform:"uppercase", letterSpacing:1, color:C.gray400, marginTop:0, marginBottom:14 }}>Palette</h4>
          <div style={{ display:"grid", gridTemplateColumns:"repeat(5,1fr)", gap:5, marginBottom:16 }}>
            {questions.map((_,i) => {
              const s = statusMap[getStatus(i)];
              const isCur = i===cur;
              const isCircle = s.shape === "circle" || s.shape === "circle-ring";
              const isRing = s.shape === "circle-ring";
              return (
                <div key={i} onClick={()=>navigate(i)}
                  style={{ width:36, height:36, display:"flex", alignItems:"center", justifyContent:"center", cursor:"pointer",
                    borderRadius: isCircle ? "50%" : 8,
                    background: isRing && !isCur ? C.green : "transparent",
                    padding: isRing && !isCur ? 3 : 0,
                    outline: isCur ? `2.5px solid ${C.primary}` : "none",
                    outlineOffset: 1 }}>
                  <div style={{ width: isRing && !isCur ? 28 : 32, height: isRing && !isCur ? 28 : 32,
                    borderRadius: isCircle ? "50%" : 6,
                    display:"flex", alignItems:"center", justifyContent:"center",
                    fontSize:11, fontWeight:700, transition:"all 0.15s",
                    background: isRing ? s.bg : s.bg,
                    color: s.color,
                    border: isRing && !isCur ? "none" : `1.5px solid ${s.border}` }}>
                    {i+1}
                  </div>
                </div>
              );
            })}
          </div>
          <div style={{ fontSize:12, color:C.gray700, display:"flex", flexDirection:"column", gap:8, marginBottom:20, padding:"12px 0", borderTop:`1px solid ${C.gray200}` }}>
            <div style={{ fontSize:11, fontWeight:700, textTransform:"uppercase", letterSpacing:1, color:C.gray400, marginBottom:2 }}>LEGEND</div>
            {[
              { label:"Answered", bg:"#16a34a", border:"#16a34a", shape:"square", count: Object.keys(answers).filter(k=>!marked.has(Number(k))).length },
              { label:"Not Answered", bg:"#dc2626", border:"#dc2626", shape:"square", count: [...visited].filter(v=>answers[v]===undefined && !marked.has(v)).length },
              { label:"Not Visited", bg:"#ffffff", border:C.gray300, shape:"square", count: questions.length - visited.size },
              { label:"Marked for Review", bg:"#7c3aed", border:"#7c3aed", shape:"circle", count: [...marked].filter(m=>answers[m]===undefined).length },
              { label:"Answered & Marked", bg:"#7c3aed", border:"#16a34a", shape:"circle-ring", count: [...marked].filter(m=>answers[m]!==undefined).length },
            ].map(item=>(
              <div key={item.label} style={{ display:"flex", alignItems:"center", gap:8 }}>
                {item.shape === "circle-ring" ? (
                  <div style={{ width:18, height:18, borderRadius:"50%", background:"#16a34a", display:"flex", alignItems:"center", justifyContent:"center", flexShrink:0 }}>
                    <div style={{ width:12, height:12, borderRadius:"50%", background:"#7c3aed" }} />
                  </div>
                ) : (
                  <div style={{ width:16, height:16, borderRadius: item.shape==="circle"?"50%":3, background:item.bg, border:`1.5px solid ${item.border}`, flexShrink:0 }} />
                )}
                <span style={{ color:C.gray700, flex:1 }}>{item.label}</span>
                <span style={{ fontWeight:700, color:C.gray500 }}>({item.count})</span>
              </div>
            ))}
          </div>
          <Btn variant="danger" onClick={()=>setShowSummary(true)} style={{ width:"100%", justifyContent:"center", fontSize:13 }}>Submit</Btn>
        </div>
      </div>
    </div>
  );
}

